// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IPointingContract {
    function addPoints() external;
    function getPoints() external view returns (uint256);
}

contract PointingContract {

    mapping(address => uint256) points;

    function addPoints() public {

        points[msg.sender] += 1;
    }

    function getPoints() public view returns (uint256) {
        return points[msg.sender];
    }
}

